import React from 'react';
import styled from 'styled-components';
import Button from '../generic/button';
const ContentArea = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  background-color: #9e9e9e;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  justify-content: center;
`;

const MenuArea = styled.div`
  height: 30%;
  width: 50%;
  margin-left: auto;
  margin-right: auto;
  border: 1px solid white;
  border-radius: 5px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  padding: 20px;
`;
const MenuMessage = styled.div`
  position: centre;
  width: 90%;
  height: 30%;
  margin-left: auto;
  margin-right: auto;
  background-color: #9e9e9e;
  box-sizing: border-box;
  display: flex;
  justify-content: center;
  text-align: center;
  font-size: 24px;
  font-family: ${'Open Sans'};
`;
const ButtonPositon = styled.div`
  width: 100%;
  height: 20%;
  margin-left: auto;
  margin-right: auto;
  margin-bottom: 10%;
`;
export default function VerificationScene(props) {
  const { buttonOnClick } = props;
  return (
    <ContentArea>
      <MenuArea>
        <MenuMessage>Verification</MenuMessage>
        <ButtonPositon>
          <Button
            text={'Verifying Withdrawal...'}
            onClickFunc={buttonOnClick}
          ></Button>
        </ButtonPositon>
      </MenuArea>
    </ContentArea>
  );
}
